mapper.select:
select du.BTS_NM,cell.CELL_NM, data.* from ( SELECT SUBSTR(#{FROMYMD},7,2) || '~' || SUBSTR(#{TOYMD},7,2) AS YMD ,'-' AS MB_TIME ,ORG.INGR_ERP_CD AS INGR_ERP_CD ,ORG.C_UID AS C_UID ,ORG.CELL_ID AS CELL_ID ,ORG.MCID AS MCID ,ORG.FREQ_KIND AS FREQ_KIND
FROM (
) ORG GROUP BY INGR_ERP_CD,C_UID,CELL_ID,MCID,FREQ_KIND
) data , LTE_BTS_DU_INFO du , LTE_BTS_CELL_INFO cell where data.INGR_ERP_CD = du.INGR_ERP_CD and data.C_UID = du.C_UID and data.INGR_ERP_CD = cell.INGR_ERP_CD and data.C_UID = cell.C_UID and data.CELL_ID = cell.CELL_ID order by data.YMD DESC, data.INGR_ERP_CD asc ,data.C_UID ASC ,data.CELL_ID ,data.MCID ASC
