mapper.update:
UPDATE LTE_CONST_CQI_THRP
    SET CQI_00 = #{CQI_00}
        ,CQI_01 = #{CQI_01}
        ,CQI_02 = #{CQI_02}
        ,CQI_03 = #{CQI_03}
        ,CQI_04 = #{CQI_04}
        ,CQI_05 = #{CQI_05}
        ,CQI_06 = #{CQI_06}
        ,CQI_07 = #{CQI_07}
        ,CQI_08 = #{CQI_08}
        ,CQI_09 = #{CQI_09}
        ,CQI_10 = #{CQI_10}
        ,CQI_11 = #{CQI_11}
        ,CQI_12 = #{CQI_12}
        ,CQI_13 = #{CQI_13}
        ,CQI_14 = #{CQI_14}
        ,CQI_15 = #{CQI_15}
        ,LASTUPDATE = SYSDATE
  WHERE MFC_CD = #{MFC_CD}
    AND RRU_TYPE = #{RRU_TYPE}

UPDATE LTE_CONST_TABLE
    SET CONST_VAL = #{CONST_VAL}
        , RAMARK = #{RAMARK}
        , LASTUPDATE = SYSDATE
 WHERE CONST_TYPE = #{CONST_TYPE}

UPDATE LTE_CONST_UPLINK
    SET CONST_VAL = #{CONST_VAL}
        , RAMARK = #{RAMARK}
        , LASTUPDATE = SYSDATE
 WHERE CONST_TYPE = #{CONST_TYPE}

    UPDATE LTE_CRITICAL_VALUE
       SET DL_RRU_VAL1 = #{DL_RRU_VAL1}
           , DL_RRU_VAL2 = #{DL_RRU_VAL2}
           , DL_REPEATER_VAL1 = #{DL_REPEATER_VAL1}
           , DL_REPEATER_VAL2 = #{DL_REPEATER_VAL2}
           , PRB_USG_VAL1 = #{PRB_USG_VAL1}
           , PRB_USG_VAL2 = #{PRB_USG_VAL2}
           , UL_POWER_VAL1 = #{UL_POWER_VAL1}
           , UL_POWER_VAL2 = #{UL_POWER_VAL2}
           ,LASTUPDATE = SYSDATE

            ,USER_ID = #{USER_ID}

WHERE USE_TYPE = #{USE_TYPE}

      AND USER_ID = #{USER_ID}

UPDATE LTE_CONST_RSSI
    SET RSSI_01 = #{RSSI_01}
        ,RSSI_02 = #{RSSI_02}
        ,RSSI_03 = #{RSSI_03}
        ,RSSI_04 = #{RSSI_04}
        ,RSSI_05 = #{RSSI_05}
        ,RSSI_06 = #{RSSI_06}
        ,RSSI_07 = #{RSSI_07}
        ,RSSI_08 = #{RSSI_08}
        ,RSSI_09 = #{RSSI_09}
        ,RSSI_10 = #{RSSI_10}
        ,RSSI_11 = #{RSSI_11}
        ,RSSI_12 = #{RSSI_12}
        ,RSSI_13 = #{RSSI_13}
        ,RSSI_14 = #{RSSI_14}
        ,RSSI_15 = #{RSSI_15}
        ,RSSI_16 = #{RSSI_16}
        ,LASTUPDATE = SYSDATE
  WHERE MFC_CD = #{MFC_CD}
